mapper.select:
SELECT c.*, t.spot_id as spot_id FROM biz_comment c JOIN biz_order o ON c.order_id = o.id JOIN biz_ticket t ON o.ticket_id = t.id where c.delete_flag = 'NOT_DELETE'
AND c.order_id = #{orderId}
AND t.spot_id = #{bizCommentPageParam.spotId}
ORDER BY c.create_time DESC

SELECT c.*,t.spot_id as spot_id FROM biz_comment c JOIN biz_order o ON c.order_id = o.id JOIN biz_ticket t ON o.ticket_id = t.id where c.delete_flag = 'NOT_DELETE'
AND c.order_id = #{orderId}
AND t.spot_id = #{bizCommentPageParam.spotId}
AND c.audit=1 ORDER BY c.create_time DESC
